type Calendar = 'ethiopic' | 'gregorian';
type Era = 'AM' | 'AA';
interface EthiopicDate {
    year: number;
    month: number;
    day: number;
    era?: Era;
}
interface GregorianDate {
    year: number;
    month: number;
    day: number;
}
type WeekdayIndex = 0 | 1 | 2 | 3 | 4 | 5 | 6;
interface YearProgress {
    daysLeft: number;
    totalDaysInYear: number;
    percentCompleted: number;
}

type HighlightCategory = 'religious' | 'national' | 'observance';
interface Highlight {
    id: string;
    name: string;
    amharicName: string;
    calendar: Calendar;
    month: number;
    day: number;
    category?: HighlightCategory;
    tags?: string[];
}
interface HighlightWithDate extends Highlight {
    date: Date;
}
/** Fully resolved highlight with both calendar counterparts. */
interface ResolvedHighlight {
    id: string;
    name: string;
    amharicName: string;
    category?: HighlightCategory;
    tags?: string[];
    gregorian: GregorianDate;
    ethiopic: EthiopicDate;
}

export type { Calendar as C, EthiopicDate as E, GregorianDate as G, Highlight as H, ResolvedHighlight as R, WeekdayIndex as W, YearProgress as Y, Era as a, HighlightWithDate as b, HighlightCategory as c };
